mapper.select:
select specs_id, specs_name, product_type
from specs
where specs_id = #{specsId};

select count(specs_name)
from specs
where specs_name = #{specsName}
  and specs_id != #{specsId}
  and product_type = #{productType};

select specs_id, specs_name, product_type
from specs
where product_type = #{productType};
