mapper.select:
select supplier_id, supplier_no, supplier_name, product_type, principal, contact_way, status
from supplier
where supplier_id = #{supplierId};

select supplier_id, supplier_no, supplier_name, product_type, principal, contact_way, status
from supplier
where status;

select count(supplier_name)
from supplier
where supplier_id != #{supplierId}
  and supplier_name = #{supplierName};
